operation.add:
if (isset($this->request->post['config_compare_status'])) {
    $data['config_compare_status'] = $this->request->post['config_compare_status'];
} else {
    $data['config_compare_status'] = $this->config->get('config_compare_status');
}

if (isset($this->request->post['config_wishlist_status'])) {
    $data['config_wishlist_status'] = $this->request->post['config_wishlist_status'];
} else {
    $data['config_wishlist_status'] = $this->config->get('config_wishlist_status');
}

if (isset($this->request->post['config_reward_point_status'])) {
    $data['config_reward_point_status'] = $this->request->post['config_reward_point_status'];
} else {
    $data['config_reward_point_status'] = $this->config->get('config_reward_point_status');
}

$_['help_compare']                     = 'Enable/Disable product compare.';
$_['help_wishlist']                    = 'Enable/Disable product wish list.';
$_['help_reward_point']                = 'Enable/Disable product reward point.';
